import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:5000/api', // Make sure this matches your backend URL
});

// Add request interceptor to add token
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// Babysitters
export const getBabysitters = async (filters) => {
  try {
    const response = await api.get('/babysitters', { params: filters });
    return response;
  } catch (error) {
    console.error('Error fetching babysitters:', error);
    throw error;
  }
};

export const getBabysitterById = (id) => api.get(`/babysitters/${id}`);

// Reservations
export const createReservation = (data) => api.post('/reservations', data);
export const getReservations = () => api.get('/reservations');
export const updateReservationStatus = (id, status) => 
  api.patch(`/reservations/${id}/status`, { status });

// Reviews
export const createReview = (data) => api.post('/reviews', data);
export const getBabysitterReviews = (babysitterId) => 
  api.get(`/reviews/babysitter/${babysitterId}`);

// Auth endpoints
export const login = (data) => api.post('/auth/login', data);
export const register = (data) => api.post('/auth/signup', data);
export const logout = () => api.post('/auth/logout');

export default api;
