import { useState, useEffect } from 'react';
import { getReservations, updateReservationStatus } from '../services/api';
import { useAuth } from '../context/AuthContext';

export default function Reservations() {
  const { user } = useAuth();
  const [reservations, setReservations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchReservations();
  }, []);

  const fetchReservations = async () => {
    try {
      setLoading(true);
      const response = await getReservations();
      setReservations(response.data);
    } catch (err) {
      setError('Failed to fetch reservations');
    } finally {
      setLoading(false);
    }
  };

  const handleStatusUpdate = async (id, status) => {
    try {
      await updateReservationStatus(id, status);
      fetchReservations();
    } catch (err) {
      setError('Failed to update reservation status');
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="w-16 h-16 border-4 border-primary-500 border-t-transparent rounded-full animate-spin"></div>
      </div>
    );
  }

  return (
    <div className="bg-gray-100 min-h-screen py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <h1 className="text-2xl font-bold text-gray-900 mb-6">My Reservations</h1>

        {error && (
          <div className="mb-4 p-4 text-sm text-red-800 rounded-lg bg-red-50">
            {error}
          </div>
        )}

        <div className="bg-white shadow overflow-hidden sm:rounded-md">
          <ul className="divide-y divide-gray-200">
            {reservations.map((reservation) => (
              <li key={reservation._id}>
                <div className="px-4 py-4 sm:px-6">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center">
                      <div className="flex-shrink-0">
                        <div className="h-12 w-12 rounded-full bg-primary-500 flex items-center justify-center text-white">
                          {user.role === 'Parent' 
                            ? reservation.babysitter.name[0]
                            : reservation.parent.name[0]}
                        </div>
                      </div>
                      <div className="ml-4">
                        <h3 className="text-lg font-medium text-gray-900">
                          {user.role === 'Parent' 
                            ? reservation.babysitter.name
                            : reservation.parent.name}
                        </h3>
                        <p className="text-sm text-gray-500">
                          {new Date(reservation.date).toLocaleDateString()} at {reservation.time}
                        </p>
                      </div>
                    </div>
                    <div className="flex items-center space-x-4">
                      <span className={`px-3 py-1 rounded-full text-sm font-medium
                        ${reservation.status === 'pending' ? 'bg-yellow-100 text-yellow-800' : ''}
                        ${reservation.status === 'accepted' ? 'bg-green-100 text-green-800' : ''}
                        ${reservation.status === 'rejected' ? 'bg-red-100 text-red-800' : ''}
                      `}>
                        {reservation.status}
                      </span>
                      {user.role === 'Babysitter' && reservation.status === 'pending' && (
                        <div className="space-x-2">
                          <button
                            onClick={() => handleStatusUpdate(reservation._id, 'accepted')}
                            className="px-3 py-1 text-sm font-medium text-white bg-green-600 rounded hover:bg-green-700"
                          >
                            Accept
                          </button>
                          <button
                            onClick={() => handleStatusUpdate(reservation._id, 'rejected')}
                            className="px-3 py-1 text-sm font-medium text-white bg-red-600 rounded hover:bg-red-700"
                          >
                            Reject
                          </button>
                        </div>
                      )}
                    </div>
                  </div>
                </div>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}
