import { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { getReservations, updateReservationStatus } from '../services/api';
import { format } from 'date-fns';

export default function Reservations() {
  const { user } = useAuth();
  const [reservations, setReservations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchReservations();
  }, []);

  const fetchReservations = async () => {
    try {
      setLoading(true);
      const response = await getReservations();
      setReservations(response.data);
    } catch (err) {
      console.error('Error fetching reservations:', err);
      setError('Failed to fetch reservations');
    } finally {
      setLoading(false);
    }
  };

  const handleStatusUpdate = async (reservationId, newStatus) => {
    try {
      await updateReservationStatus(reservationId, newStatus);
      // Refresh reservations after update
      fetchReservations();
    } catch (err) {
      console.error('Error updating reservation:', err);
      setError('Failed to update reservation status');
    }
  };

  const getStatusBadgeColor = (status) => {
    switch (status) {
      case 'pending':
        return 'bg-yellow-100 text-yellow-800';
      case 'accepted':
        return 'bg-green-100 text-green-800';
      case 'rejected':
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="w-16 h-16 border-4 border-primary-500 border-t-transparent rounded-full animate-spin"></div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <h1 className="text-2xl font-semibold text-gray-900 mb-6">
        {user.role === 'parent' ? 'My Bookings' : 'My Reservations'}
      </h1>

      {error && (
        <div className="mb-4 p-4 text-sm text-red-800 bg-red-50 rounded-md">
          {error}
        </div>
      )}

      {reservations.length === 0 ? (
        <div className="text-center py-12 bg-white rounded-lg shadow">
          <p className="text-gray-500">No reservations found</p>
        </div>
      ) : (
        <div className="bg-white shadow overflow-hidden sm:rounded-md">
          <ul className="divide-y divide-gray-200">
            {reservations.map((reservation) => (
              <li key={reservation._id} className="p-4 hover:bg-gray-50">
                <div className="flex items-center justify-between">
                  <div className="flex items-center">
                    {reservation.babysitter?.photo ? (
                      <img
                        src={reservation.babysitter.photo}
                        alt={reservation.babysitter.name}
                        className="h-10 w-10 rounded-full"
                      />
                    ) : (
                      <div className="h-10 w-10 rounded-full bg-primary-500 flex items-center justify-center text-white font-semibold">
                        {reservation.babysitter?.name?.[0] || '?'}
                      </div>
                    )}
                    <div className="ml-4">
                      <div className="text-sm font-medium text-gray-900">
                        {user.role === 'parent' 
                          ? `Babysitter: ${reservation.babysitter?.name}`
                          : `Parent: ${reservation.parent?.name}`
                        }
                      </div>
                      <div className="text-sm text-gray-500">
                        {format(new Date(reservation.date), 'PPP')} at {reservation.time}
                      </div>
                      <div className="text-sm text-gray-500">
                        Duration: {reservation.duration} hours
                      </div>
                      <div className="text-sm text-gray-500">
                        Total: ${reservation.totale}
                      </div>
                    </div>
                  </div>

                  <div className="flex flex-col items-end space-y-2">
                    <span className={`px-2 py-1 text-xs font-medium rounded-full ${getStatusBadgeColor(reservation.status)}`}>
                      {reservation.status}
                    </span>

                    {user.role === 'babysitter' && reservation.status === 'pending' && (
                      <div className="flex space-x-2">
                        <button
                          onClick={() => handleStatusUpdate(reservation._id, 'accepted')}
                          className="px-3 py-1 text-xs font-medium text-white bg-green-600 rounded-md hover:bg-green-700"
                        >
                          Accept
                        </button>
                        <button
                          onClick={() => handleStatusUpdate(reservation._id, 'rejected')}
                          className="px-3 py-1 text-xs font-medium text-white bg-red-600 rounded-md hover:bg-red-700"
                        >
                          Reject
                        </button>
                      </div>
                    )}
                  </div>
                </div>

                <div className="mt-2">
                  <p className="text-sm text-gray-600">
                    {reservation.description}
                  </p>
                </div>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
