import { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

export default function Register() {
  const navigate = useNavigate();
  const { signUp } = useAuth();
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    age: '',
    contact: '',
    adresse: '',
    photo: '',
    role: 'parent',
    tarif: '',
    experience: '',
    competances: [],
    disponibilite: true,
    certifications: [],
    languages: [],
    bio: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      setError('');
      setLoading(true);

      const dataToSubmit = {
        ...formData,
        age: parseInt(formData.age),
        ...(formData.role === 'babysitter' && {
          tarif: parseFloat(formData.tarif) || 0,
          experience: parseInt(formData.experience) || 0,
          competances: formData.competances || []
        })
      };

      console.log('Submitting registration data:', dataToSubmit);
      const response = await signUp(dataToSubmit);

      // Redirect based on role
      if (response.user.role === 'babysitter') {
        navigate('/babysitter-dashboard');
      } else {
        navigate('/parent-dashboard');
      }
    } catch (err) {
      console.error('Registration error:', err);
      setError(err.response?.data?.message || 'Failed to create an account');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col justify-center py-12 sm:px-6 lg:px-8">
      <div className="sm:mx-auto sm:w-full sm:max-w-md">
        <h2 className="mt-6 text-center text-3xl font-extrabold text-gray-900">
          Create your account
        </h2>
      </div>

      <div className="mt-8 sm:mx-auto sm:w-full sm:max-w-md">
        <div className="bg-white py-8 px-4 shadow sm:rounded-lg sm:px-10">
          {error && (
            <div className="mb-4 bg-red-50 border border-red-200 text-red-600 px-4 py-3 rounded relative">
              {error}
            </div>
          )}

          <form className="space-y-6" onSubmit={handleSubmit}>
            <div>
              <label htmlFor="name" className="block text-sm font-medium text-gray-700">
                Name
              </label>
              <input
                id="name"
                name="name"
                type="text"
                required
                value={formData.name}
                onChange={handleChange}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500"
              />
            </div>

            <div>
              <label htmlFor="email" className="block text-sm font-medium text-gray-700">
                Email
              </label>
              <input
                id="email"
                name="email"
                type="email"
                required
                value={formData.email}
                onChange={handleChange}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500"
              />
            </div>

            <div>
              <label htmlFor="password" className="block text-sm font-medium text-gray-700">
                Password
              </label>
              <input
                id="password"
                name="password"
                type="password"
                required
                value={formData.password}
                onChange={handleChange}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500"
              />
            </div>

            <div>
              <label htmlFor="age" className="block text-sm font-medium text-gray-700">
                Age
              </label>
              <input
                id="age"
                name="age"
                type="number"
                required
                value={formData.age}
                onChange={handleChange}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500"
              />
            </div>

            <div>
              <label htmlFor="contact" className="block text-sm font-medium text-gray-700">
                Contact
              </label>
              <input
                id="contact"
                name="contact"
                type="tel"
                required
                value={formData.contact}
                onChange={handleChange}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500"
              />
            </div>

            <div>
              <label htmlFor="adresse" className="block text-sm font-medium text-gray-700">
                Address
              </label>
              <input
                id="adresse"
                name="adresse"
                type="text"
                required
                value={formData.adresse}
                onChange={handleChange}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500"
              />
            </div>

            <div>
              <label htmlFor="photo" className="block text-sm font-medium text-gray-700">
                Photo URL
              </label>
              <input
                id="photo"
                name="photo"
                type="text"
                required
                value={formData.photo}
                onChange={handleChange}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500"
              />
            </div>

            <div>
              <label htmlFor="role" className="block text-sm font-medium text-gray-700">
                Role
              </label>
              <select
                id="role"
                name="role"
                value={formData.role}
                onChange={handleChange}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500"
              >
                <option value="parent">Parent</option>
                <option value="babysitter">Babysitter</option>
              </select>
            </div>

            {formData.role === 'babysitter' && (
              <>
                <div>
                  <label htmlFor="tarif" className="block text-sm font-medium text-gray-700">
                    Tarif
                  </label>
                  <input
                    id="tarif"
                    name="tarif"
                    type="number"
                    required
                    value={formData.tarif}
                    onChange={handleChange}
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500"
                  />
                </div>

                <div>
                  <label htmlFor="experience" className="block text-sm font-medium text-gray-700">
                    Experience
                  </label>
                  <input
                    id="experience"
                    name="experience"
                    type="number"
                    required
                    value={formData.experience}
                    onChange={handleChange}
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500"
                  />
                </div>

                <div>
                  <label htmlFor="competances" className="block text-sm font-medium text-gray-700">
                    Competances
                  </label>
                  <input
                    id="competances"
                    name="competances"
                    type="text"
                    required
                    value={formData.competances.join(', ')}
                    onChange={(e) => setFormData(prev => ({
                      ...prev,
                      competances: e.target.value.split(',').map(c => c.trim())
                    }))}
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500"
                  />
                </div>

                <div>
                  <label htmlFor="disponibilite" className="block text-sm font-medium text-gray-700">
                    Disponibilite
                  </label>
                  <input
                    id="disponibilite"
                    name="disponibilite"
                    type="checkbox"
                    checked={formData.disponibilite}
                    onChange={(e) => setFormData(prev => ({
                      ...prev,
                      disponibilite: e.target.checked
                    }))}
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700">Bio</label>
                  <textarea
                    name="bio"
                    value={formData.bio}
                    onChange={handleChange}
                    maxLength={500}
                    rows={4}
                    className="mt-1 block w-full rounded-md border-gray-300"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700">Languages</label>
                  <input
                    type="text"
                    name="languages"
                    value={formData.languages.join(', ')}
                    onChange={(e) => setFormData(prev => ({
                      ...prev,
                      languages: e.target.value.split(',').map(lang => lang.trim())
                    }))}
                    placeholder="English, French, etc."
                    className="mt-1 block w-full rounded-md border-gray-300"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700">Certifications</label>
                  <div className="space-y-2">
                    {formData.certifications.map((cert, index) => (
                      <div key={index} className="flex gap-2">
                        <input
                          type="text"
                          value={cert.name}
                          onChange={(e) => {
                            const newCerts = [...formData.certifications];
                            newCerts[index].name = e.target.value;
                            setFormData(prev => ({ ...prev, certifications: newCerts }));
                          }}
                          placeholder="Certification name"
                          className="flex-1 rounded-md border-gray-300"
                        />
                        <input
                          type="date"
                          value={cert.date}
                          onChange={(e) => {
                            const newCerts = [...formData.certifications];
                            newCerts[index].date = e.target.value;
                            setFormData(prev => ({ ...prev, certifications: newCerts }));
                          }}
                          className="rounded-md border-gray-300"
                        />
                        <input
                          type="text"
                          value={cert.issuer}
                          onChange={(e) => {
                            const newCerts = [...formData.certifications];
                            newCerts[index].issuer = e.target.value;
                            setFormData(prev => ({ ...prev, certifications: newCerts }));
                          }}
                          placeholder="Issuer"
                          className="flex-1 rounded-md border-gray-300"
                        />
                      </div>
                    ))}
                    <button
                      type="button"
                      onClick={() => setFormData(prev => ({
                        ...prev,
                        certifications: [...prev.certifications, { name: '', date: '', issuer: '' }]
                      }))}
                      className="text-sm text-primary-600 hover:text-primary-500"
                    >
                      + Add Certification
                    </button>
                  </div>
                </div>
              </>
            )}

            <div>
              <button
                type="submit"
                disabled={loading}
                className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
              >
                {loading ? 'Creating account...' : 'Register'}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}
